var _ = require('lodash');

const users = [
    { name: 'John', age: 25 },
    { name: 'Lenny', age: 51 },
    { name: 'Andrew', age: 43 },
    { name: 'Peter', age: 81 },
    { name: 'Anna', age: 43 },
    { name: 'Albert', age: 76 },
    { name: 'Adam', age: 47 },
    { name: 'Robert', age: 72 }
  ];

  let filter1 = _.filter(users, function(user){return user.age >= 70; });
  let sort1 = _.sortBy(filter1, function(user){return user.name});
  console.log("=======Senior=======");
  console.log(sort1);
  let filter2 = _.filter(users, function(user){return user.age <= 70; });
  let sort2 = _.sortBy(filter2, function(user){return user.name});
  console.log("=======Junior=======");
  console.log(sort2);