const R = require('ramda');

const users = [
  { name: 'John', age: 25 },
  { name: 'Lenny', age: 51 },
  { name: 'Andrew', age: 43 },
  { name: 'Peter', age: 81 },
  { name: 'Anna', age: 43 },
  { name: 'Albert', age: 76 },
  { name: 'Adam', age: 47 },
  { name: 'Robert', age: 72 }
];
console.log("=======Senior=======");
let filter1 = R.filter(user => user.age >= 70, users);
console.log(R.sortBy(R.prop('name'), filter1));
console.log("=======Junior=======")
let filter2 = R.filter(user => user.age <= 70, users);
console.log(R.sortBy(R.prop('name'), filter2));